 require_once( '../../common/config.ini' ); require_once( '../../common/users.php' ); require_once( '../../common/bodys.php' ); session_start(); $usersObj = new users(); $bodysObj = new bodys(); if( $usersObj->get_auth_session( $_SESSION, $user )) { if( $usersObj->db_login( $user['email'], $user['password'], $auth=ADMIN_ROLL, $user )) { session_regenerate_id( TRUE ); } else { $usersObj->session_dell(); } } else { header( 'Location:'.URL.'/admin/' ); } if( $_SERVER['REQUEST_METHOD'] == 'POST' || $_SERVER['REQUEST_METHOD'] == 'GET' ) { $bodysObj->get_data($_REQUEST, $form_data); (!isset($form_data['status'])) ? $form_data['status'] = '':NULL; } switch ($form_data['status']) { case 'edit': if( !$bodysObj->get_body( $form_data['id'], $form_data )) { exit("err!! bodys no data."); } $form_data['status'] = 'edit'; require_once( 'edit.php'); break; case 'edit_done': $usersObj->check_word_count( $form_data['title'], 200 ); $usersObj->check_word_count( $form_data['body'], 10000 ); $err = $usersObj->get_err(); if( empty( $err )) { $bodysObj->db_edit_body( $form_data ); } else { require_once( 'edit.php'); break; } header( 'Location:'.URL.'/admin/bodys/' ); break; default: $start = 0; $num = 1; if( !$bodysObj->get_all_body( MAILADD, $start, $num, $data_add )) { $message_add = "登録メールが登録されていません。"; } if( !$bodysObj->get_all_body( MAILDELETE, $start, $num, $data_delete )) { $message_delete = "解除メールが登録されていません。"; } $form_data['status'] = 'default'; require_once( 'list.php'); break; } ?>