<?php
 require_once( '../../common/config.ini' ); require_once( '../../common/mails.php' ); require_once( '../../common/users.php' ); require_once( '../../common/settings.php' ); session_start(); $usersObj = new users(); $mailsObj = new mails(); $settingsObj = new settings(); if( $usersObj->get_auth_session( $_SESSION, $user )) { if( $usersObj->db_login( $user['email'], $user['password'], $auth=ADMIN_ROLL, $user )) { session_regenerate_id( TRUE ); } else { $usersObj->session_dell(); } } else { header( 'Location:'.URL.'/admin/' ); } if( $_SERVER['REQUEST_METHOD'] == 'POST' || $_SERVER['REQUEST_METHOD'] == 'GET' ) { $settingsObj->get_data($_REQUEST, $form_data); (!isset($form_data['status'])) ? $form_data['status'] = '':NULL; } switch ($form_data['status']) { case 'edit': $settingsObj->check_word_count( $form_data['firstname'], 32 ); $err = $settingsObj->get_err(); if( empty( $err )) { if( $settingsObj->get_all_setting( $data )) { $settingsObj->db_edit_setting( $form_data ); $usersObj->db_update_admin_name( $form_data['firstname'], $form_data['admin_id'] ); $message = '設定を更新しました。'; } } require_once( 'edit.php'); break; case 'delete': $settingsObj->db_delete_setting( $form_data['id'] ); header( 'Location:'.URL.'/admin/settings/' ); break; default: if( !$settingsObj->get_all_setting( $data )) { $settingsObj->db_set_setting(); if( $settingsObj->get_all_setting( $data )) { $message = "初期設定しました。"; } else { $err['all'] = "設定情報にエラーが見つかりました。恐れ入りますがもう一度設定をして下さい。"; $settingsObj->db_all_delete_setting(); } } $mailsObj->get_admin_user($admin_user); $form_data = $data; $form_data['admin_id'] = $admin_user['id']; $form_data['firstname'] = $admin_user['firstname']; $form_data['email'] = $admin_user['email']; $form_data['status'] = 'default'; require_once( 'edit.php'); break; } ?>